import React from "react"
import "../../../assets/style/cinema/index.css"
import ExactSelect from "./exactSelect"
import {
    connect
} from "react-redux";
import {
    bindActionCreators
}from "redux"
import actionCreate from '../../../store/actionCreate/cinema';
import Up from "./up"
import {Link} from "react-router-dom";
import PubSub from 'pubsub-js'
class Cinemahome extends React.Component {
    constructor(props){
        super(props)
        this.state={
            zindex:false,
            isCityShow: false,
            isBrandShow: false,
            isFeatureShow: false,
        }
    }
    zindex(){
        PubSub.subscribe("lala",function (msg,data) {
            this.setState({
                zindex:data
            })
        }.bind(this))
    }
    zindexFalse(){
        PubSub.publish("xixi",this.state)
        this.setState({
            zindex:false
        })
    }
    render() {
        return (
            <div className={"cinema-home"}>
                <div onClick={this.zindexFalse.bind(this)} className={"wrap-lv-wrap"} style={{display:this.state.zindex?"block":"none"}}></div>
                <div>
                    <header>
                        <div className={"headers"}>
                            <h1>影院</h1>
                        </div>
                    </header>
                    <section>
                        <div className={"topbar"}>
                            <div className={"city"}>
                                <Link to={'/city'} className='white-bg-city-name'>北京</Link>
                                <i className='city-entry-arrow'></i>
                            </div>
                            <div onClick={()=>{
                                this.props.history.push("/cinemasearch")
                            }} className={"search"}>
                                <img src="data:image/png;base64," alt=""/>
                                <span>搜影院</span>
                            </div>
                        </div>

                    </section>
                    <ExactSelect zindex={this.state.zindex}></ExactSelect>
                    <div style={{height:'40px',background:"#fff"}}></div>
                    <Up></Up>
                </div>
            </div>

        )
    }

    componentDidMount() {
        this.props.getCinemaList();
        //修改站标
        const str ="http://s0.meituan.net/bs/?f=myfe/canary:/static/deploy/images/icon/faviconmy.ico";
        if(this.props.location.pathname==="/nav/cinema"){
            document.title="影院";
            document.querySelector(".titl-logo").href=str;
        }
        this.zindex()
    }
}
function mapStateToProps(state) {
    return{
         cinemaList:state.cinema.cinemaIndex.cinemaList
    }
}
function mapDispatchToProps(dispatch) {
    return bindActionCreators(actionCreate,dispatch)
}
export default connect(mapStateToProps,mapDispatchToProps)(Cinemahome)